mapper.update:
update CAF.T_ROLE
set ROLE_NAME = #{roleName,jdbcType=VARCHAR},
  DESCRIPTION = #{description,jdbcType=VARCHAR},
  ROLE_TYPE = #{roleType,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select ID, ROLE_NAME, DESCRIPTION, ROLE_TYPE
from CAF.T_ROLE
where ID = #{id,jdbcType=VARCHAR}
